import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertStartupSchema, type InsertStartup } from "@shared/schema";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Plus } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const categories = [
  "EdTech",
  "FinTech",
  "HealthTech",
  "Food & Delivery",
  "Sustainability",
  "Social",
  "Career Development",
  "E-commerce",
  "SaaS",
  "Other",
];

const stages = [
  "Idea",
  "Prototype",
  "MVP",
  "Early Stage",
  "Growth",
  "Scaling",
];

export function SubmitStartupDialog() {
  const [open, setOpen] = useState(false);
  const { toast } = useToast();

  const form = useForm<InsertStartup>({
    resolver: zodResolver(insertStartupSchema),
    defaultValues: {
      name: "",
      tagline: "",
      description: "",
      stage: "Idea",
      category: "",
      seeking: [],
    } as InsertStartup,
  });

  const createMutation = useMutation({
    mutationFn: (data: InsertStartup) => apiRequest("POST", "/api/startups", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/startups"] });
      toast({
        title: "Startup Submitted!",
        description: "Your startup has been added to the showcase.",
      });
      setOpen(false);
      form.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to submit startup",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: any) => {
    const payload: InsertStartup = {
      ...data,
      teamSize: data.teamSize ? parseInt(data.teamSize) : undefined,
    };
    createMutation.mutate(payload);
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button data-testid="button-submit-startup">
          <Plus className="h-4 w-4 mr-2" />
          Submit Startup
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto" data-testid="dialog-submit-startup">
        <DialogHeader>
          <DialogTitle>Submit Your Startup</DialogTitle>
          <DialogDescription>
            Showcase your startup to the Rochester community
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Startup Name *</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="Your startup name" 
                      {...field} 
                      data-testid="input-startup-name"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="tagline"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Tagline *</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="Brief one-liner about your startup" 
                      {...field} 
                      data-testid="input-tagline"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description *</FormLabel>
                  <FormControl>
                    <Textarea
                      placeholder="Describe your startup, what problem it solves, and your vision"
                      rows={4}
                      {...field}
                      data-testid="textarea-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="category"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Category *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-category">
                          <SelectValue placeholder="Select category" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {categories.map((cat) => (
                          <SelectItem key={cat} value={cat}>
                            {cat}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="stage"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Stage *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-stage">
                          <SelectValue placeholder="Select stage" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {stages.map((stage) => (
                          <SelectItem key={stage} value={stage}>
                            {stage}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="teamSize"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Team Size</FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        placeholder="Number of team members"
                        {...field}
                        value={field.value || ""}
                        onChange={(e) => field.onChange(e.target.value ? parseInt(e.target.value) : undefined)}
                        data-testid="input-team-size"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="founded"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Founded Year</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="2024"
                        {...field}
                        value={field.value || ""}
                        data-testid="input-founded"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="websiteUrl"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Website URL</FormLabel>
                  <FormControl>
                    <Input
                      type="url"
                      placeholder="https://yourstartup.com"
                      {...field}
                      value={field.value || ""}
                      data-testid="input-website-url"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="imageUrl"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Image URL</FormLabel>
                  <FormControl>
                    <Input
                      type="url"
                      placeholder="https://example.com/image.jpg"
                      {...field}
                      value={field.value || ""}
                      data-testid="input-image-url"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end gap-2 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setOpen(false)}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createMutation.isPending}
                data-testid="button-submit"
              >
                {createMutation.isPending ? "Submitting..." : "Submit Startup"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/SearchBar.tsx.content:
import { Input } from "@/components/ui/input";
import { Search, X } from "lucide-react";
import { Button } from "@/components/ui/button";

interface SearchBarProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
}

export function SearchBar({ value, onChange, placeholder = "Search..." }: SearchBarProps) {
  return (
    <div className="relative w-full max-w-md">
      <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
      <Input
        type="text"
        placeholder={placeholder}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        className="pl-9 pr-9"
        data-testid="input-search"
      />
      {value && (
        <Button
          variant="ghost"
          size="icon"
          className="absolute right-1 top-1/2 -translate-y-1/2 h-7 w-7"
          onClick={() => onChange("")}
          data-testid="button-clear-search"
        >
          <X className="h-4 w-4" />
        </Button>
      )}
    </div>
  );
}

client/src/pages/Profile.tsx.content:
import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Card, CardHeader, CardTitle, CardDescription, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { User as UserIcon, X, Building2, GraduationCap, BookOpen, Lightbulb } from "lucide-react";
import type { User } from "@shared/schema";

export default function Profile() {
  const { user: authUser } = useAuth();
  const { toast } = useToast();
  const [skillInput, setSkillInput] = useState("");

  const { data: user, isLoading } = useQuery<User>({
    queryKey: ["/api/auth/user"],
  });

  // Initialize formData state
  const [formData, setFormData] = useState<{
    userType: string;
    organization: string;
    bio: string;
    major: string;
    year: string;
    availability: string;
    skills: string[];
    website: string;
    collaborationInterests: string;
    researchArea: string;
    mentorshipInterests: string;
    startupName: string;
    startupStage: string;
    teamSize: string;
    collaborationNeeds: string;
  }>({
    userType: "",
    organization: "",
    bio: "",
    major: "",
    year: "",
    availability: "",
    skills: [],
    website: "",
    collaborationInterests: "",
    researchArea: "",
    mentorshipInterests: "",
    startupName: "",
    startupStage: "",
    teamSize: "",
    collaborationNeeds: "",
  });

  // Update form data when user data loads
  useEffect(() => {
    if (user) {
      setFormData({
        userType: user.userType || "",
        organization: user.organization || "",
        bio: user.bio || "",
        major: user.major || "",
        year: user.year || "",
        availability: user.availability || "",
        skills: user.skills || [],
        website: user.website || "",
        collaborationInterests: user.collaborationInterests || "",
        researchArea: user.researchArea || "",
        mentorshipInterests: user.mentorshipInterests || "",
        startupName: user.startupName || "",
        startupStage: user.startupStage || "",
        teamSize: user.teamSize?.toString() || "",
        collaborationNeeds: user.collaborationNeeds || "",
      });
    }
  }, [user]);

  const updateProfileMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      const res = await apiRequest("PATCH", "/api/users/profile", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Profile updated",
        description: "Your profile has been successfully updated.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update profile. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Build role-specific data object
    const cleanedData: any = {
      bio: formData.bio || undefined,
      skills: formData.skills,
    };

    // Always include userType if present
    if (formData.userType) {
      cleanedData.userType = formData.userType.trim();
    }

    // Role-specific fields
    if (formData.userType === "Company") {
      // Company: organization, website, collaboration interests
      cleanedData.organization = formData.organization?.trim() || "";
      cleanedData.website = formData.website?.trim() || "";
      cleanedData.collaborationInterests = formData.collaborationInterests?.trim() || "";
      // Clear student/professor/startup fields
      cleanedData.major = "";
      cleanedData.year = "";
      cleanedData.availability = "";
      cleanedData.researchArea = "";
      cleanedData.mentorshipInterests = "";
      cleanedData.startupName = "";
      cleanedData.startupStage = "";
      cleanedData.teamSize = undefined;
      cleanedData.collaborationNeeds = "";
    } else if (formData.userType === "University Student") {
      // University Student: major, year, availability, skills
      cleanedData.major = formData.major?.trim() || undefined;
      cleanedData.year = formData.year?.trim() || undefined;
      cleanedData.availability = formData.availability?.trim() || undefined;
      // Clear company/professor/startup fields
      cleanedData.organization = "";
      cleanedData.website = "";
      cleanedData.collaborationInterests = "";
      cleanedData.researchArea = "";
      cleanedData.mentorshipInterests = "";
      cleanedData.startupName = "";
      cleanedData.startupStage = "";
      cleanedData.teamSize = undefined;
      cleanedData.collaborationNeeds = "";
    } else if (formData.userType === "Student in Rochester") {
      // Student in Rochester (with startup): startup fields only
      cleanedData.startupName = formData.startupName?.trim() || "";
      cleanedData.startupStage = formData.startupStage?.trim() || "";
      cleanedData.teamSize = formData.teamSize ? parseInt(formData.teamSize) : undefined;
      cleanedData.collaborationNeeds = formData.collaborationNeeds?.trim() || "";
      // Clear company/professor/student academic fields
      cleanedData.organization = "";
      cleanedData.website = "";
      cleanedData.collaborationInterests = "";
      cleanedData.researchArea = "";
      cleanedData.mentorshipInterests = "";
      cleanedData.major = "";
      cleanedData.year = "";
      cleanedData.availability = "";
    } else if (formData.userType === "Professor") {
      // Professor: department (major), research area, mentorship interests
      cleanedData.major = formData.major?.trim() || undefined;
      cleanedData.researchArea = formData.researchArea?.trim() || "";
      cleanedData.mentorshipInterests = formData.mentorshipInterests?.trim() || "";
      // Clear student/company/startup fields
      cleanedData.organization = "";
      cleanedData.website = "";
      cleanedData.collaborationInterests = "";
      cleanedData.year = "";
      cleanedData.availability = "";
      cleanedData.startupName = "";
      cleanedData.startupStage = "";
      cleanedData.teamSize = undefined;
      cleanedData.collaborationNeeds = "";
    } else {
      // Default: clear all role-specific fields with empty strings
      cleanedData.organization = "";
      cleanedData.website = "";
      cleanedData.collaborationInterests = "";
      cleanedData.major = "";
      cleanedData.year = "";
      cleanedData.availability = "";
      cleanedData.researchArea = "";
      cleanedData.mentorshipInterests = "";
      cleanedData.startupName = "";
      cleanedData.startupStage = "";
      cleanedData.teamSize = undefined;
      cleanedData.collaborationNeeds = "";
    }

    // Remove undefined fields (keep empty strings to clear database values)
    Object.keys(cleanedData).forEach(key => {
      if (cleanedData[key] === undefined) {
        delete cleanedData[key];
      }
    });

    updateProfileMutation.mutate(cleanedData);
  };

  const addSkill = () => {
    if (skillInput.trim() && !formData.skills.includes(skillInput.trim())) {
      setFormData({
        ...formData,
        skills: [...formData.skills, skillInput.trim()],
      });
      setSkillInput("");
    }
  };

  const removeSkill = (skillToRemove: string) => {
    setFormData({
      ...formData,
      skills: formData.skills.filter(skill => skill !== skillToRemove),
    });
  };

  if (isLoading || !user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block h-8 w-8 animate-spin rounded-full border-4 border-solid border-primary border-r-transparent"></div>
          <p className="mt-4 text-muted-foreground">Loading profile...</p>
        </div>
